mapper.select:
select emp_num
from employees
where emp_id = #{empId}

 	select lib_num libNum, lib_writer libWriter, lib_subject libSubject, lib_content libContent, lib_pw libPw, emp_num empNum, ip_addr ipAddr
	from emp_library
	where lib_num = #{libNum}

mapper.update:
update emp_library
set lib_writer = #{libWriter}, lib_subject = #{libSubject}, lib_content = #{libContent}, lib_pw = #{libPw}, emp_num = #{empNum}
where lib_num = #{libNum}
